<?php     //echo time() - $_SESSION["LAST_ACTIVITY"];    if (isset($_SESSION["LAST_ACTIVITY"])) {        if (time() - $_SESSION["LAST_ACTIVITY"] > 1800) {            //echo time() - $_SESSION["LAST_ACTIVITY"];            // last request was more than 30 minutes ago            session_unset();     // unset $_SESSION variable for the run-time             session_destroy();   // destroy session data in storage            $msg = "Timeout. Login again!";            header('location: ../../index.php?info='.$msg);        } else{            //time() - $_SESSION["LAST_ACTIVITY"];            $_SESSION["LAST_ACTIVITY"] = time(); // update last activity time stamp        }    }?>